                }

            })
        )
    }
    const [text, setText] = useState("")
    const [tasks, setTasks] = useState([
        {
        id: 1,
        text: 'Doctor Appointment',
        completed: true,
        toggleCompleted: toggleCompleted
        },
        {
        id: 2,
        text: 'Meeting at School',
        completed: false,
        toggleCompleted: toggleCompleted,

        }
        ]);






